mapper.select:
select ga.id, ga.author_name, ga.author_id, ga.article_title, ga.abstract_text, ga.index_picture, ga.article_category, gac.path, ga.like_count, ga.view_count, ga.create_time,
 ga.update_time,ga.is_display from gp_articles ga left join gp_article_category gac on ga.article_category=gac.id
 where ga.is_display=1 and gac.path=#{pathName} order by ga.create_time desc,ga.view_count desc,ga.like_count desc limit ${limitCount}


where ga.is_display = 1 and

    #{id}

order by view_count desc, like_count desc, create_time desc limit ${limitCount}

select ga.id, ga.author_name, ga.author_id, ga.article_title, ga.abstract_text, ga.index_picture, ga.article_category, gac.path, ga.like_count, ga.view_count, ga.create_time, ga.update_time,ga.is_display from gp_articles ga left join gp_article_category gac on ga.article_category=gac.id
and ga.is_display=1 and gac.path=#{pathName}
order by ga.create_time desc,ga.like_count desc,ga.view_count desc

select ga.id, ga.author_name, ga.author_id, ga.article_title, ga.abstract_text, ga.index_picture, ga.article_category, gac.path, ga.like_count, ga.view_count, ga.create_time, ga.update_time,ga.is_display from gp_articles ga left join gp_article_category gac on ga.article_category=gac.id
and ga.is_display=1 and gac.path=#{pathName}
order by RAND() limit 1

select ga.id, ga.author_name, ga.author_id, ga.article_title, ga.abstract_text, ga.index_picture, ga.article_category, gac.path, ga.like_count, ga.view_count, ga.create_time, ga.update_time,ga.is_display from gp_articles ga left join gp_article_category gac on ga.article_category=gac.id
and ga.is_display=1 and gac.path=#{pathName}
order BY RAND() limit 4



     and author_name like concat('%', #{authorName}, '%')
     and author_id = #{authorId}
     and article_title like concat('%',#{articleTitle},'%')
     and article_content = #{articleContent}
     and article_category = #{articleCategory}
     and LOCATE(#{likeCount},like_count) 
     and LOCATE(#{viewCount},view_count) 
     and is_display = #{isDisplay}

order by create_time desc, update_time desc



     and author_name like concat('%', #{authorName}, '%')
     and author_id = #{authorId}
     and article_title = #{articleTitle}
     and article_content = #{articleContent}
     and article_category = #{articleCategory}
     and like_count = #{likeCount}
     and view_count = #{viewCount}
    and is_display = 1

order by create_time desc,like_count desc,view_count desc

mapper.update:
update gp_articles
author_name = #{authorName}, author_id = #{authorId}, article_title = #{articleTitle}, article_content = #{articleContent}, abstract_text = #{abstractText}, index_picture = #{indexPicture}, article_category = #{articleCategory}, like_count = #{likeCount}, view_count = #{viewCount}, create_time = #{createTime}, update_time = #{updateTime}, is_display = #{isDisplay},
where id = #{id}

update gp_articles
like_count = #{likeCount}, view_count = #{viewCount},
where id = #{id}

mapper.select:
SELECT m.* FROM
  (SELECT c.id, c.article_title, c.rownum FROM (SELECT a.id, a.article_title,a.article_category, a.create_time, (@rownum1 := @rownum1 + 1) AS rownum
FROM gp_articles a, (SELECT @rownum1 := 0) b where a.is_display=1) c left join gp_article_category gac on c.article_category=gac.id

      and gac.path = #{pathName}

  order by c.create_time ASC ) m,
  (SELECT c.rownum FROM (SELECT a.id, a.article_title, a.article_category, (@rownum2 := @rownum2 + 1) AS rownum
FROM gp_articles a, (SELECT @rownum2 := 0) b WHERE a.is_display=1) c left join gp_article_category gac on c.article_category=gac.id

     and gac.path = #{pathName}
     and c.id =#{articleId}

 ) n
 WHERE m.rownum > n.rownum
LIMIT 1

SELECT m.* FROM
 (SELECT c.id, c.article_title, c.rownum FROM (SELECT a.id, a.article_title,a.article_category, a.create_time, (@rownum1 := @rownum1 + 1) AS rownum
FROM gp_articles a, (SELECT @rownum1 := 0) b where a.is_display=1) c left join gp_article_category gac on c.article_category=gac.id

     and gac.path = #{pathName}

 order by c.create_time DESC ) m,
 (SELECT c.rownum FROM (SELECT a.id, a.article_title, a.article_category, (@rownum2 := @rownum2 + 1) AS rownum
FROM gp_articles a, (SELECT @rownum2 := 0) b WHERE a.is_display=1) c left join gp_article_category gac on c.article_category=gac.id

     and gac.path = #{pathName}
     and c.id =#{articleId}

 ) n
 WHERE m.rownum < n.rownum
LIMIT 1
